package array.list;import java.util.ArrayList;import java.util.Collection;import java.util.Collections;import java.util.List;public class ManagerStudent {    ArrayList<Student> studentList;    public ManagerStudent() {        this.studentList = new ArrayList<>();    }    public void add(Student student) {        studentList.add(student);    }    public void display() {        for (int i = 0; i < studentList.size(); i++) {            System.out.println(studentList.get(i));        }        System.out.println("---------------------");    }    public int search(String name) {        for (int i = 0; i < studentList.size(); i++) {            if (studentList.get(i).getName().equals(name)) {                return i;            }        }        return -1;    }    public void edit(String name, Student student) {        int index = search(name);        if (index == -1) {            System.out.println("khong co");        } else {            studentList.set(index, student);        }    }    public void del(String name) {        int index = search(name);        if (index == -1) {            System.out.println("khong co");        } else {            studentList.remove(index);        }    }    public void sort() {        Collections.sort(this.studentList);    }}